properties.scriptBody:
cd ${p:resource/BPM 857 Single Cluster Install/BPM_Install_Dir}
cd bin
BPMConfig.bat -create -de C:\IBM\Software\DE_Property_Files\Advanced-PS-SingleCluster-DB2.properties

echo ${p:component.name}
echo  ${p:component/Process application}
echo ${p:component/Process application package}

echo ${p:component/Process application snapshot acronym}
echo ${p:component/Process applicaton acronym}

postProcessingScript.body:
if (properties.get("exitCode")!=0) {
  properties.put("Status", "Failure");
} else {
  properties.put("Status", "Success");
}

scanner.register("Acronym: *", function(lineNumber, line) {
  properties.put("appSnapshotAcronym", line);

});

scanner.scan();

